$(window).on("load", function() {
    $(window).trigger("scroll");
    $(window).trigger("resize");
});

$(function() {
    $(window).trigger("resize");
    init_nav_menu();
    init_sliders();
});

$(window).on("resize", function() {});

function init_nav_menu() {
    var i = $(".hamburger").unbind();
    var e = $(".navbar__nav");
    e.removeClass("navigation-open");
    i.on("click", function(i) {
        i.preventDefault();
        $(".hamburger").toggleClass("is-active");
        e.slideToggle(function() {
            if (e.is(":hidden")) {
                e.removeAttr("style");
                $(".hamburger").removeClass("is-active");
            }
        });
    });
}

function init_sliders() {
    $(".slick__clients").slick({
        infinite: true,
        speed: 300,
        slidesToShow: 3,
        slidesToScroll: 1,
        responsive: [ {
            breakpoint: 1024,
            settings: {
                slidesToShow: 3,
                slidesToScroll: 1
            }
        }, {
            breakpoint: 800,
            settings: {
                slidesToShow: 2,
                slidesToScroll: 2
            }
        }, {
            breakpoint: 600,
            settings: {
                slidesToShow: 1,
                slidesToScroll: 1,
                arrows: false
            }
        } ]
    });
    $(".slick__testimonial").slick({
        responsive: [ {
            breakpoint: 800,
            settings: {
                arrows: false
            }
        } ]
    });
}